views.view:
DROP  VIEW   IF EXISTS    appriesinfo; 
CREATE  VIEW `appriesinfo` AS  
 (select `a`.`id` AS `id`,`a`.`serviceDate` AS `serviceDate`,`a`.`CustorTime` AS `CustorTime`,`a`.`DeptId` AS `DeptId`,`a`.`EmployeeId` AS `EmployeeId`,`a`.`serviceType` AS `serviceType`,`a`.`appriesTime` AS `appriesTime`,`a`.`remark` AS `remark`,`a`.`deptarl` AS `deptarl`,`a`.`keyno` AS `keyno`,`a`.`mechinetype` AS `mechinetype`,`dept`.`name` AS `deptName`,`dept`.`fatherId` AS `fatherId`,`emp`.`Name` AS `employeeName`,`kt`.`name` AS `result`,`kt`.`isJoy` AS `isjoy`,`emp`.`CardNum` AS `EmployeeCardNo`,`st`.`businessName` AS `businessName` from ((((`appries`.`appries_appries` `a` join `appries`.`appries_dept` `dept`) join `appries`.`appries_employee` `emp`) join `appries`.`appries_keytype` `kt`) join `appries`.`appries_servicetype` `st`) where ((`a`.`DeptId` = `dept`.`id`) and (`a`.`EmployeeId` = `emp`.`Id`) and (`a`.`keyno` = `kt`.`keyNo`) and (`a`.`serviceType` = `st`.`id`)));

DROP  VIEW   IF EXISTS    appriseview; 
CREATE  VIEW `appriseview` AS  
 (select `fdept`.`id` AS `Deptid`,`fdept`.`name` AS `deptName`,`a`.`serviceDate` AS `serviceDate`,`a`.`keyno` AS `Result`,`a`.`CustorTime` AS `serviceTime`,`dept`.`name` AS `PoliceStationName`,`dept`.`id` AS `PoliceStationID`,`emp`.`Name` AS `policename`,`kt`.`name` AS `ResultName`,`emp`.`CardNum` AS `policeID` from (((((`appries`.`appries_appries` `a` join `appries`.`appries_dept` `dept`) join `appries`.`appries_employee` `emp`) join `appries`.`appries_keytype` `kt`) join `appries`.`appries_servicetype` `st`) join `appries`.`appries_dept` `fdept`) where ((`emp`.`deptid` = `dept`.`id`) and (`a`.`EmployeeId` = `emp`.`Id`) and (`a`.`keyno` = `kt`.`keyNo`) and (`fdept`.`id` = `dept`.`fatherId`)));

DROP  VIEW   IF EXISTS    taiyuan; 
CREATE  VIEW `taiyuan` AS  
 (select `fdept`.`id` AS `Deptid`,`fdept`.`name` AS `deptName`,`a`.`serviceDate` AS `serviceDate`,`a`.`keyno` AS `keyNo`,`a`.`CustorTime` AS `serviceTime`,`dept`.`name` AS `PoliceStationName`,`dept`.`id` AS `PoliceStationID`,`emp`.`Name` AS `policename`,`kt`.`name` AS `result`,`emp`.`CardNum` AS `policeID` from (((((`appries`.`appries_appries` `a` join `appries`.`appries_dept` `dept`) join `appries`.`appries_employee` `emp`) join `appries`.`appries_keytype` `kt`) join `appries`.`appries_servicetype` `st`) join `appries`.`appries_dept` `fdept`) where ((`emp`.`deptid` = `dept`.`id`) and (`a`.`EmployeeId` = `emp`.`Id`) and (`a`.`keyno` = `kt`.`keyNo`) and (`fdept`.`id` = `dept`.`fatherId`)));

procs.proc:
DROP  PROCEDURE   IF EXISTS    appriesAddDemo ;CREATE   PROCEDURE `appriesAddDemo`  (IN mac varchar(20), IN tt VARCHAR(50), IN cardnum1 VARCHAR(6), IN pj TINYINT,  IN demo VARCHAR(1000),in businessType  int)
begin

declare deptId Integer;

declare employeeId Integer;

select id into deptId  from appries_dept where    remark=mac limit 0,1;

select id into employeeId  from appries_employee where  CardNum= cardnum1  LIMIT 0,1 ;

insert into appries_appries (serviceDate,serviceCycle,custortime,deptid,employeeid,keyno,mechinetype,serviceType,JieshouTime,remark,businessType)

value (date(cast(tt as datetime)),0,time(cast(tt as datetime)),deptId,employeeId,pj,2,1,tt,demo,businessType);

end;

DROP  PROCEDURE   IF EXISTS    appriesAddJZ ;CREATE   PROCEDURE `appriesAddJZ`  (IN deptId varchar(20), IN tt VARCHAR(50), IN employeeId VARCHAR(6), IN pj TINYINT,  IN demo VARCHAR(1000),in businessType  int)
begin
insert into appries_appries (serviceDate,serviceCycle,custortime,deptid,employeeid,keyno,mechinetype,serviceType,JieshouTime,remark,businessType)

value (date(cast(tt as datetime)),0,time(cast(tt as datetime)),deptId,employeeId,pj,2,1,now(),demo,businessType);

end;

DROP  PROCEDURE   IF EXISTS    appries_info ;CREATE   PROCEDURE `appries_info`  ()
BEGIN
declare apprie_1,apprie_2,apprie_3,apprie_4,apprie_5,apprie_6,apprie_7 varchar(100);
declare msql2 varchar(2000);
set apprie_1=(select name from appries_keytype where keyno=0);
set apprie_2=(select name from appries_keytype where keyno=1);
set apprie_3=(select name from appries_keytype where keyno=2);
set apprie_4=(select name from appries_keytype where keyno=3);
set apprie_5=(select name from appries_keytype where keyno=4);
set apprie_6=(select name from appries_keytype where keyno=5);
set apprie_7=(select name from appries_keytype where keyno=6);
SET @MyQuery=Concat('select ID,DeptId as ID,EmployeeId as ID,serviceDate as ,CustorTime as ,(case keyno when 0 then 1 else 0 end) as ',apprie_1,',(case keyno when 1 then 1 else 0 end) as ',apprie_2,',(case keyno when 2 then 1 else 0 end) as ',apprie_3,',(case keyno when 3 then 1 else 0 end) as ',apprie_4,',(case keyno when 4 then 1 else 0 end) as ',apprie_5,',(case keyno when 5 then 1 else 0 end) as ',apprie_6,',(case keyno when 6 then 1 else 0 end) as ',apprie_7,' from appries_appries');
PREPARE msql2 from @MyQuery;
EXECUTE msql2;
END;

DROP  PROCEDURE   IF EXISTS    GetAppr ;CREATE   PROCEDURE `GetAppr`  (IN mac BIGINT, IN devid VARCHAR(50), IN vcardnum VARCHAR(50))
BEGIN
declare m_dt DATETIME;
declare m_unitid,m_updeptid,m_deptid,m_employeeid INTEGER;
declare m_unitname,m_updeptname,m_deptname,m_employeename,m_keyname VARCHAR(50);

set m_dt=now();
select id,name into m_unitid,m_unitname from appries_dept where fatherid=-1;
select id,name into m_updeptid,m_updeptname from appries_dept where conv(remark,16,10)=mac;
select id,name into m_deptid,m_deptname from appries_dept where fatherid=m_updeptid and remark=devid;
select id,name into m_employeeid,m_employeename from appries_employee where cardnum=vcardnum and (deptid=m_updeptid or ext1 is not NULL);
create TEMPORARY TABLE t(dt DATETIME,
                unitid int,
                updeptid INTEGER,
                deptid INTEGER,
                employeeid INTEGER,
                unitname VARCHAR(50),
                updeptname VARCHAR(50),
                deptname VARCHAR(50),
                employeename VARCHAR(50),
                keyname VARCHAR(50));
insert into t values(m_dt,m_unitid,m_updeptid,m_deptid,m_employeeid,m_unitname,m_updeptname,m_deptname,m_employeename,m_keyname);
select * from t;
drop table t;
END;

DROP  PROCEDURE   IF EXISTS    GetDetpNameByMac ;CREATE   PROCEDURE `GetDetpNameByMac`  (mac BIGINT)
BEGIN
select name from appries_dept where conv(remark,16,10)=mac;
END;

DROP  PROCEDURE   IF EXISTS    GetDEVs ;CREATE   PROCEDURE `GetDEVs`  ()
BEGIN
declare maxlevel int;
set maxlevel=(select max(lenvel) from appries_dept);
select a.id as groupid,b.id as winid,a.name as deptname,b.name as formname,conv(a.remark,16,10) as mac,b.remark as devid,b.dev_undownload,b.dev_savedphoto,b.lastcheck,b.client_type from appries_dept a inner join appries_dept b on b.fatherid=a.id;
END;

DROP  PROCEDURE   IF EXISTS    InsertData ;CREATE   PROCEDURE `InsertData`  (IN mac BIGINT, IN devid VARCHAR(50), IN vcardnum VARCHAR(50), IN pj TINYINT, IN appriesTimes VARCHAR(50), IN atten VARCHAR(1000))
BEGIN
declare m_dt datetime;
declare m_updeptid,m_deptid,m_employeeid int;
set m_dt=now();
set m_updeptid=(select id from appries_dept where conv(remark,16,10)=mac);
set m_deptid=(select id from appries_dept where fatherid=m_updeptid and remark=devid);
set m_employeeid=(select id from appries_employee where (0+cardnum)=(0+vcardnum) and (deptid=m_updeptid or ext1 is not NULL));
insert into appries_appries (serviceDate,serviceCycle,custortime,deptid,employeeid,keyno,mechinetype,serviceType,JieshouTime,remark)
value (date(cast(appriesTimes as datetime)),0,time(cast(appriesTimes as datetime)),m_deptid,m_employeeid,pj,2,1,appriesTimes,atten);
END;

DROP  PROCEDURE   IF EXISTS    InsertError ;CREATE   PROCEDURE `InsertError`  (IN mac BIGINT, IN devid BIGINT, IN vcardnum VARCHAR(50), IN pj TINYINT)
BEGIN
insert into appries_erroinfo (appriestime,reamark,m_devid,cardnum,keyid) value (now(),hex(mac),devid,vcardnum,pj);
END;

